function solve(l, r) {
  if (l === r) return 0
 
  if(dp[l][r] !== Number.MAX_SAFE_INTEGER) return dp[l][r]
  var ans = Math.min(solve(l + 1, r), solve(l, r - 1))
  if(dp[l][r] === Number.MAX_SAFE_INTEGER) dp[l][r] = ans + a[r] - a[l]
  return ans + a[r] - a[l]
}

