function main() {
 
    var [n, x, pos] = readline().split(' ').map((x, i) => {
        if (i === 2) return Number(x)
        if (i === 0) return Number(x)
        // if (minA > Number(x)) minA = Number(x)
        return Number(x)
    })
 
    var lower = x - 1
    var higher = n - x
    var ans = 1n
 
    var r = n
    var l = 0
    var mid = 0
    var count = 0
    while (l < r) {
        mid = Math.floor((l + r) / 2)
        // console.log(l, r)
        // console.log(mid, pos)
        if (mid <= pos) {
            if (mid !== pos) {
                ans = (ans * BigInt(lower)) % BigInt(module)
                lower--
            }
            l = mid + 1
        }
        if (mid > pos) {
            ans = (ans * BigInt(higher)) % BigInt(module)
            higher--
            r = mid
        }
        count++
    }
    if (count < n) ans = (BigInt(P(n - count)) * ans) % BigInt(module)
    console.log(ans.toString())
    // 1 1 7
    // 6 9, 15, 18
}
 
function P(p) {
    var a = 1
    for (var i = 1; i <= p; i++) {
