function PC(k, p) {
    var a = 1
    for (var i = p - k + 1; i <= p; i++) {
        a = (a * i) % module
    }
    return a
}
 
function C(k, n) {
    if (k > n) return 0
    var bottom = PC(k, n)
    // var up = P(n) % module
